#23514624000 in 0.04398846626281738 seconds.
import time

start = time.time()
num = ""
largest = 0

for i in range(0, len(num) - 13):
    product = 1
    for j in range(i, i+13):
        test = num[j:j+1]
        product *= int(test)
    if product > largest:
        largest = product

end = time.time()
print(largest, "in", end-start, "seconds.")
